function isIsomorphic(s, t) {
 let mapS = new Map(), mapT = new Map();
 for (let i = 0; i < s.length; i++) {
 let charS = s[i], charT = t[i];
 if ((mapS.has(charS) && mapS.get(charS) !== charT) || (mapT.has(charT) && mapT.get(charT) !== charS)) return false;
